codeBlocks.code:
// 基本的なテキスト
Text("Hello, World!")

// Markdown記法のサポート
Text("**太字** と *斜体* と [リンク](https://apple.com)")

// 日付の表示スタイル
Text(Date(), style: .date)     // 2024年1月1日
Text(Date(), style: .time)     // 12:00
Text(Date(), style: .relative) // 2時間前
Text(Date(), style: .timer)    // 0:00 (カウントアップ)

// 数値のフォーマット
Text(1000, format: .currency(code: "JPY")) // ¥1,000

// システムアイコン (SF Symbols)
Image(systemName: "star.fill")
    .foregroundStyle(.yellow)

// アセット画像
Image("ProfilePicture")
    .resizable()        // サイズ変更を許可
    .aspectRatio(contentMode: .fit) // アスペクト比を維持
    .frame(width: 100)

// 非同期画像の読み込み
AsyncImage(url: URL(string: "https://example.com/icon.png")) {
    image in image.resizable()
} placeholder: {
    ProgressView()
}

// 基本的なボタン
Button("タップして実行") {
    print("実行されました")
}

// テキスト以外のラベル
Button(action: {
    // アクション
}) {
    HStack {
        Image(systemName: "trash")
        Text("削除")
    }
}

// ロール指定（破壊的なアクションなど）
Button("削除", role: .destructive) {
    // 削除処理
}

// 垂直方向（縦）に並べる
VStack(alignment: .leading, spacing: 10) {
    Text("タイトル")
    Text("詳細説明")
}

// 水平方向（横）に並べる
HStack {
    Image(systemName: "person")
    Text("ユーザー名")
}

// 重ねて配置（奥から手前へ）
ZStack {
    Rectangle().fill(.blue)
    Text("前面のテキスト").foregroundStyle(.white)
}

// Capsule（ピル型の角丸長方形）
Capsule()
    .fill(.blue)        // .fill: シェイプを色で塗りつぶす
    .frame(width: 200, height: 50)

// .fill の詳細
// fill: シェイプの内部を指定の色で完全に塗りつぶすメソッド
// stroke: 枠線だけを描く（内部は透明）
// 単色だけでなく、グラデーションも指定可能

Capsule()
    .fill(
        LinearGradient(
            gradient: Gradient(colors: [.blue, .purple]),
            startPoint: .topLeading,
            endPoint: .bottomTrailing
        )
    )
    .frame(width: 200, height: 50)

// ボタンの背景として使う例
Text("タグ")
    .padding(.horizontal, 16)
    .padding(.vertical, 8)
    .background(Capsule().fill(.blue))
    .foregroundStyle(.white)

// 枠線だけのCapsule
Capsule()
    .stroke(.blue, lineWidth: 2)   // stroke: 枠線のみ
    .frame(width: 200, height: 50)

// その他の基本シェイプ
Circle()                          // 円
Rectangle()                       // 長方形
RoundedRectangle(cornerRadius: 10) // 角丸長方形
Ellipse()                         // 楕円

// Spacer: 利用可能な空間を埋める
HStack {
    Text("左端")
    Spacer() // ここが広がる
    Text("右端")
}

// Divider: 区切り線を表示
VStack {
    Text("上段")
    Divider()
    Text("下段")
}

// 1. 配列からの動的生成（単純なデータ）
let items = ["Apple", "Banana", "Orange"]

// id: \.self の解説
// 文字列や数値など、一意のIDを持たない単純な型の配列を表示する場合、
// 「要素そのもの('\.self')」を識別子として使います。
List(items, id: \.self) { item in
    Text(item)
}

// 2. 構造体のリスト（推奨）
struct Fruit: Identifiable {
    let id = UUID()     // 一意のID
    let name: String
}

let fruits = [
    Fruit(name: "Apple"),
    Fruit(name: "Banana")
]

// Identifiableに準拠していれば id指定は不要
List(fruits) { fruit in
    Text(fruit.name)
}

// 3. 削除機能付きのリスト
// .onDelete を使うことでスワイプ削除が可能になります
List {
    ForEach(items, id: \.self) { item in
        Text(item)
    }
    .onDelete { indexSet in
        items.remove(atOffsets: indexSet)
    }
}

// 4. 静的なリスト
List {
    Text("設定")
    Text("プロフィール")
}

@State var name = ""

// 基本的な入力フィールド
TextField("名前を入力", text: $name)
    .textFieldStyle(.roundedBorder)

// パスワード入力（伏せ字）
SecureField("パスワード", text: $password)

// 数値入力キーボード指定
TextField("年齢", value: $age, format: .number)
    .keyboardType(.numberPad)

@State var selection = "apple"
let fruits = ["apple", "banana", "orange"]

// 基本的なPicker
Picker("フルーツを選択", selection: $selection) {
    ForEach(fruits, id: \.self) { fruit in
        Text(fruit)
    }
}

// label: パラメータについて
// Pickerの第一引数「label:」はアクセシビリティや
// Form内での表示ラベルとして使われます。
// 多くのスタイルでは画面上に表示されませんが、
// VoiceOverなどの支援技術で読み上げられるため
// 省略せず適切な説明を設定しましょう。

// スタイルのバリエーション
.pickerStyle(.segmented)  // セグメント（横並びボタン）
.pickerStyle(.wheel)      // ホイール（iOS標準）
.pickerStyle(.menu)       // ドロップダウンメニュー
.pickerStyle(.inline)     // インライン（List内で展開）

// 垂直スクロール
ScrollView {
    VStack {
        ForEach(0..<20) { i in
            Text("行 \(i)")
                .frame(maxWidth: .infinity)
                .padding()
                .background(.gray.opacity(0.1))
        }
    }
}

// 水平スクロール
ScrollView(.horizontal, showsIndicators: false) {
    HStack {
        // ...コンテンツ
    }
}
