// Spacerは利用可能なスペースを埋める
HStack {
    Text("左")
    Spacer()          // 左と右の間を最大限広げる
    Text("右")
}

VStack {
    Text("上")
    Spacer()
    Text("下")
}

HStack {
    Text("左")
    Spacer()
    Text("中央")
    Spacer()
    Text("右")        // 均等に配置される
}

// Stack初期化時に要素間の間隔を指定
VStack(spacing: 12) {     // 縦方向の間隔を12ptに固定
    Text("要素1")
    Text("要素2")
}

HStack(spacing: 20) {     // 横方向の間隔を20ptに固定
    Text("左")
    Text("右")
}

// spacing: 0 で間隔をなくす
VStack(spacing: 0) {
    Text("くっついた")
    Text("テキスト")
}

// offset()で位置をずらす
Text("オフセット")
    .offset(x: 20, y: 10)         // 右に20pt、下に10ptずらす

// 重ねて表示する例
ZStack {
    Circle()
        .fill(.blue)
        .frame(width: 100, height: 100)
    Circle()
        .fill(.red)
        .frame(width: 50, height: 50)
        .offset(x: 30, y: -30)    // 右上にずらす
}

// .system()でフォントを細かくカスタマイズ

// サイズのみ指定
Text("サイズ指定")
    .font(.system(size: 24))

// サイズと太さを指定
Text("サイズと太さ")
    .font(.system(size: 20, weight: .bold))

// サイズ、太さ、デザインをすべて指定
Text("フルカスタマイズ")
    .font(.system(size: 18, weight: .medium, design: .rounded))

// ===== デザインの種類 =====

// .default - 標準のサンセリフ体
Text("Default Design")
    .font(.system(size: 20, design: .default))

// .rounded - 丸みを帯びたフォント（フレンドリーな印象）
Text("Rounded Design")
    .font(.system(size: 20, design: .rounded))

// .monospaced - 等幅フォント（コード表示に最適）
Text("Monospaced Design")
    .font(.system(size: 20, design: .monospaced))

// .serif - セリフ体（書籍のような印象）
Text("Serif Design")
    .font(.system(size: 20, design: .serif))

// ===== 実践的な使用例 =====

// 見出しに丸みのあるフォント
Text("Welcome!")
    .font(.system(size: 32, weight: .bold, design: .rounded))

// コードブロック用の等幅フォント
Text("let message = \"Hello\"")
    .font(.system(size: 14, weight: .regular, design: .monospaced))
    .padding()
    .background(Color(.secondarySystemBackground))
    .cornerRadius(8)

// 引用文にセリフ体
Text("\"The only way to do great work is to love what you do.\"")
    .font(.system(size: 18, weight: .light, design: .serif))
    .italic()

// 行間の設定
Text("1行目\n2行目\n3行目")
    .lineSpacing(10)              // 行間を10pt追加

// 文字間隔の設定
Text("文字間隔を広げる")
    .kerning(5)                   // 文字間を5pt追加

// 行数制限
Text("とても長いテキストがここに入ります。表示しきれない場合は省略されます。")
    .lineLimit(2)                 // 最大2行まで表示
    .truncationMode(.tail)        // 末尾を...で省略

// 色で背景を設定
Text("色の背景")
    .padding()
    .background(.blue)

// 透明度付きの背景
Text("半透明の背景")
    .padding()
    .background(.blue.opacity(0.3))

// マテリアル（すりガラス効果）
Text("マテリアル背景")
    .padding()
    .background(.ultraThinMaterial)    // 薄いすりガラス
    // .regularMaterial, .thickMaterial なども使用可能

// ビューを背景に
Text("画像背景")
    .padding()
    .background {
        Image(systemName: "star.fill")
            .resizable()
            .foregroundStyle(.yellow.opacity(0.3))
    }

// テキスト用のセマンティックカラー
Text("Primary").foregroundStyle(.primary)       // メインのテキスト色
Text("Secondary").foregroundStyle(.secondary)   // 補助的なテキスト色
Text("Tertiary").foregroundStyle(.tertiary)     // さらに薄いテキスト色

// アクセントカラー
Button("アクセント") { }
    .tint(.accentColor)           // アプリのテーマカラー

// ライト/ダークモード対応
// これらの色は自動的にモードに合わせて変化します

// 線形グラデーション
Rectangle()
    .fill(
        LinearGradient(
            colors: [.blue, .purple],
            startPoint: .topLeading,
            endPoint: .bottomTrailing
        )
    )
    .frame(height: 100)

// 放射状グラデーション
Circle()
    .fill(
        RadialGradient(
            colors: [.yellow, .orange, .red],
            center: .center,
            startRadius: 0,
            endRadius: 100
        )
    )
    .frame(width: 200, height: 200)

// 角度グラデーション
Circle()
    .fill(
        AngularGradient(
            colors: [.red, .yellow, .green, .blue, .purple, .red],
            center: .center
        )
    )
    .frame(width: 200, height: 200)

// RGB値で色を作成
let customRed = Color(red: 1.0, green: 0.2, blue: 0.2)

// 16進数から色を作成
let hexColor = Color(red: 0x3A / 255, green: 0x86 / 255, blue: 0xFF / 255)

// 透明度を設定
let semiTransparent = Color.blue.opacity(0.5)

// Assets.xcassets でカラーセットを定義した場合
// let assetColor = Color("MyCustomColor")

// 状態変化をアニメーション
struct AnimationExample: View {
    @State private var isExpanded = false

    var body: some View {
        VStack {
            Rectangle()
                .fill(.blue)
                .frame(width: isExpanded ? 200 : 100,
                       height: isExpanded ? 200 : 100)
                .animation(.default, value: isExpanded)  // デフォルトアニメーション

            Button("トグル") {
                isExpanded.toggle()
            }
        }
    }
}

// withAnimationで明示的にアニメーション
struct WithAnimationExample: View {
    @State private var scale = 1.0

    var body: some View {
        VStack {
            Circle()
                .fill(.purple)
                .frame(width: 100, height: 100)
                .scaleEffect(scale)

            Button("アニメーション") {
                withAnimation(.spring(response: 0.3, dampingFraction: 0.5)) {
                    scale = scale == 1.0 ? 1.5 : 1.0
                }
            }
        }
    }
}

// 表示・非表示時のアニメーション
struct TransitionExample: View {
    @State private var showDetail = false

    var body: some View {
        VStack {
            Button("トグル") {
                withAnimation {
                    showDetail.toggle()
                }
            }

            if showDetail {
                Text("詳細テキスト")
                    .transition(.slide)         // スライドで表示/非表示
            }
        }
    }
}

// トランジションの種類
.transition(.opacity)                          // フェード
.transition(.scale)                            // 拡大/縮小
.transition(.slide)                            // スライド
.transition(.move(edge: .top))                 // 上から出入り
.transition(.push(from: .bottom))              // 下からプッシュ

// 組み合わせ
.transition(.scale.combined(with: .opacity))   // 複数を組み合わせ
.transition(.asymmetric(                       // 表示と非表示で異なる
    insertion: .scale,
    removal: .opacity
))

// アニメーション可能な修飾子
.opacity(0.5)                    // 透明度
.scaleEffect(1.5)                // 拡大縮小
.rotationEffect(.degrees(45))    // 回転
.offset(x: 50, y: 50)            // 位置移動
.frame(width: 200, height: 200)  // サイズ変更

// 実践例
struct PracticalAnimation: View {
    @State private var isAnimating = false

    var body: some View {
        Image(systemName: "heart.fill")
            .font(.system(size: 50))
            .foregroundStyle(.red)
            .scaleEffect(isAnimating ? 1.2 : 1.0)
            .opacity(isAnimating ? 1.0 : 0.5)
            .animation(
                .easeInOut(duration: 0.5).repeatForever(autoreverses: true),
                value: isAnimating
            )
            .onAppear {
                isAnimating = true
            }
    }
}

// ボタンスタイルの種類
Button("Default") { }
    // デフォルトスタイル（指定なし）

Button("Plain") { }
    .buttonStyle(.plain)              // 装飾なし、テキストのみ

Button("Bordered") { }
    .buttonStyle(.bordered)           // 枠線付き

Button("Bordered Prominent") { }
    .buttonStyle(.borderedProminent)  // 塗りつぶし（目立つ）

Button("Borderless") { }
    .buttonStyle(.borderless)         // 枠線なし

// tintでボタンの色を変更
Button("カスタム色") { }
    .buttonStyle(.borderedProminent)
    .tint(.green)                     // 緑色のボタン

// controlSizeでサイズを変更
Button("小さいボタン") { }
    .buttonStyle(.bordered)
    .controlSize(.small)              // .mini, .small, .regular, .large

Button("大きいボタン") { }
    .buttonStyle(.bordered)
    .controlSize(.large)

// カスタムボタンスタイルの作成
struct CustomButtonStyle: ButtonStyle {
    func makeBody(configuration: Configuration) -> some View {
        configuration.label
            .padding(.horizontal, 20)
            .padding(.vertical, 12)
            .background(
                configuration.isPressed ? Color.blue.opacity(0.7) : Color.blue
            )
            .foregroundStyle(.white)
            .cornerRadius(10)
            .scaleEffect(configuration.isPressed ? 0.95 : 1.0)
            .animation(.easeInOut(duration: 0.1), value: configuration.isPressed)
    }
}

// 使用例
Button("カスタムボタン") {
    print("タップされました")
}
.buttonStyle(CustomButtonStyle())

// リストスタイルの種類
List {
    Text("項目1")
    Text("項目2")
    Text("項目3")
}
.listStyle(.automatic)            // 自動（プラットフォームに応じて）

List { ... }
.listStyle(.plain)                // シンプル、区切り線のみ

List { ... }
.listStyle(.inset)                // インセット付き

List { ... }
.listStyle(.insetGrouped)         // グループ化+インセット（設定画面風）

List { ... }
.listStyle(.grouped)              // グループ化

List { ... }
.listStyle(.sidebar)              // サイドバー用

// リスト行の背景を変更
List {
    Text("カスタム背景")
        .listRowBackground(Color.yellow.opacity(0.3))
}

// リスト行の区切り線を非表示
List {
    Text("区切り線なし")
        .listRowSeparator(.hidden)

// ピッカースタイルの種類
@State private var selection = 0
let options = ["オプション1", "オプション2", "オプション3"]

Picker("選択", selection: $selection) {
    ForEach(0..<options.count, id: \.self) { index in
        Text(options[index]).tag(index)
    }
}
.pickerStyle(.automatic)              // 自動

Picker("メニュー", selection: $selection) { ... }
.pickerStyle(.menu)                   // ドロップダウンメニュー

Picker("セグメント", selection: $selection) { ... }
.pickerStyle(.segmented)              // セグメントコントロール

Picker("ホイール", selection: $selection) { ... }
.pickerStyle(.wheel)                  // ホイール型

Picker("インライン", selection: $selection) { ... }
.pickerStyle(.inline)                 // インライン表示

// ラベルスタイルの種類
Label("設定", systemImage: "gear")
    .labelStyle(.automatic)           // 自動（アイコン+テキスト）

Label("設定", systemImage: "gear")
    .labelStyle(.titleOnly)           // テキストのみ

Label("設定", systemImage: "gear")
    .labelStyle(.iconOnly)            // アイコンのみ

Label("設定", systemImage: "gear")
    .labelStyle(.titleAndIcon)        // テキスト+アイコン（明示的）

// Labelの使用例
Button {
    // アクション
} label: {
    Label("追加", systemImage: "plus.circle.fill")
}
.buttonStyle(.borderedProminent)

// contentShapeはタップ可能な領域を定義する
// 透明な部分もタップを検知したい場合に使用

// 問題: HStack内の余白部分はタップできない
HStack {
    Text("左")
    Spacer()
    Text("右")
}
.onTapGesture {
    print("タップされました")  // Spacer部分はタップ不可
}

// 解決: contentShapeで領域全体をタップ可能に
HStack {
    Text("左")
    Spacer()
    Text("右")
}
.contentShape(Rectangle())            // 四角形の領域全体をタップ可能に
.onTapGesture {
    print("タップされました")  // Spacer部分もタップ可能
}

// Buttonでも同様に使用可能
Button {
    print("タップ")
} label: {
    HStack {
        Image(systemName: "star")
        Spacer()
        Text("お気に入り")
    }
    .contentShape(Rectangle())        // ボタン全体をタップ可能に
}
.buttonStyle(.plain)

// 様々な形状を指定可能
.contentShape(Circle())               // 円形の領域
.contentShape(Capsule())              // カプセル形状
.contentShape(RoundedRectangle(cornerRadius: 10))  // 角丸四角形

// インタラクションの種類を指定
.contentShape(.interaction, Rectangle())     // タップ・ジェスチャー用
.contentShape(.dragPreview, Rectangle())     // ドラッグプレビュー用
.contentShape(.contextMenuPreview, Circle()) // コンテキストメニュー用
