// TimerStateを定義
enum TimerState {
    case idle      // 待機中
    case running   // 実行中
    case paused    // 一時停止中
}

struct ContentView: View {
    @State var timerState: TimerState = .idle

    var body: some View {
        VStack {
            Text("タイマーアプリ")
                .font(.largeTitle)

            Text("現在の状態: \(timerState)")
        }
    }
}
