struct ContentView: View {
    @State private var volume: Double = 50

    var body: some View {
        VStack(spacing: 30) {
            Text("éŸ³é‡èª¿æ•´")
                .font(.largeTitle)
                .fontWeight(.bold)

            VStack(spacing: 10) {
                Text("éŸ³é‡: \(Int(volume))")
                    .font(.title2)

                Slider(value: $volume, in: 0...100, step: 1)
                    .padding(.horizontal)

